import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function RestaurantDetails() {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:5000/api/v1/restaurants/${restaurantId}`)
      .then(response => response.json())
      .then(data => {
        setRestaurant(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error loading the restaurant details:', error);
        setLoading(false);
      });
  }, [restaurantId]);

  if (loading) return <p>Loading...</p>;
  if (!restaurant) return <p>No restaurant found</p>;

  return (
    <div>
      <h1>{restaurant?.name}</h1>
      <p>City ID: {restaurant?.city_id}</p>
      <p>User Rating: {restaurant?.userrating || 'Not Rated'}</p>
      <h2>Reviews</h2>
      {reviews.length > 0 ? (
        <ul>
          {reviews.map(review => (
            <li key={review.id}>
              <p>{review.comment}</p>
              <p>Rating: {review.rating}</p>
              <p>Date: {new Date(review.date).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      ) : <p>No reviews available.</p>}
    </div>
  );
}

export default RestaurantDetails;
