import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Modal from './Modal'; // Import the Modal component

function RestaurantDetails() {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to control the modal visibility

  useEffect(() => {
    async function fetchData() {
      try {
        const resDetails = fetch(`http://localhost:5000/api/v1/restaurants/${restaurantId}`);
        const resReviews = fetch(`http://localhost:5000/api/v1/restaurants/${restaurantId}/userreviews`);
        
        // Wait for both fetch operations to complete
        const [detailsResponse, reviewsResponse] = await Promise.all([resDetails, resReviews]);
        
        if (!detailsResponse.ok) throw new Error('Failed to fetch restaurant details');
        if (!reviewsResponse.ok) throw new Error('Failed to fetch reviews');

        const restaurantData = await detailsResponse.json();
        const reviewsData = await reviewsResponse.json();

        setRestaurant(restaurantData);
        setReviews(reviewsData);
      } catch (err) {
        console.error('Fetching error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
  
    fetchData();
  }, [restaurantId]);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <button onClick={toggleModal}>Show Details</button>
      <Modal isOpen={isModalOpen} close={toggleModal}>
        <h1>{restaurant?.name}</h1>
        <p>City ID: {restaurant?.city_id}</p>
        <p>User Rating: {restaurant?.userrating || 'Not Rated'}</p>
        <h2>Reviews</h2>
        {reviews.length > 0 ? (
          <ul>
            {reviews.map(review => (
              <li key={review.id}>
                <p>{review.comment}</p>
                <p>Rating: {review.rating}</p>
                <p>Date: {new Date(review.date).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        ) : <p>No reviews available.</p>}
      </Modal>
    </div>
  );
}

export default RestaurantDetails;
