import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function RestaurantDetails() {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRestaurantDetails = async () => {
      try {
        const resResponse = await fetch(`http://localhost:5000/api/v1/restaurants/${restaurantId}`);
        if (!resResponse.ok) throw new Error('Failed to fetch restaurant details');
        const resData = await resResponse.json();
        setRestaurant(resData);

        const revResponse = await fetch(`http://localhost:5000/api/v1/restaurants/${restaurantId}/userreviews`);
        if (!revResponse.ok) throw new Error('Failed to fetch reviews');
        const revData = await revResponse.json();
        setReviews(revData);

        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchRestaurantDetails();
  }, [restaurantId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>{restaurant?.name}</h1>
      <p>City ID: {restaurant?.city_id}</p>
      <p>User Rating: {restaurant?.userrating || 'Not Rated'}</p>
      <h2>Reviews</h2>
      {reviews.length > 0 ? (
        <ul>
          {reviews.map(review => (
            <li key={review.id}>
              <p>{review.comment}</p>
              <p>Rating: {review.rating}</p>
              <p>Date: {new Date(review.date).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      ) : <p>No reviews available.</p>}
    </div>
  );
}

export default RestaurantDetails;
